mapper.update:
UPDATE catmall_wms.wms_ware_sku
SET stock=stock + #{skuNum}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}

update catmall_wms.wms_ware_sku
set stock_locked = stock_locked + #{num}
where sku_id = #{skuId}
  and ware_id = #{wareId}
  and stock - stock_locked >= #{num}

mapper.select:
select sum(stock - stock_locked)
from catmall_wms.wms_ware_sku
where sku_id = #{skuId}

select ware_id
from catmall_wms.wms_ware_sku
where sku_id = #{skuId}
  and stock - stock_locked > 0;
